import { StatusBar } from 'expo-status-bar';
import React, { Component } from 'react';
import { StyleSheet, TouchableOpacity, View } from 'react-native';
import { globalStyle } from '../../../assets/global.style';
import Card from '../../shared/card';
import { AntDesign } from '@expo/vector-icons'

export default class Controls extends Component {
    constructor(props) {
        super(props);
    }
    render() {
        let playingIcon;
        if (this.props.playing == true) {
            playingIcon = 'pause';
        } else {
            playingIcon = 'caretright';
        }
        if (this.props.repeatOn == true) {
            let repeatControlStyle = globalStyle.controls_button_active;
        } else {
            let repeatControlStyle = globalStyle.controls_button;
        }

        return (
            <Card style={ globalStyle.controls }>
                <TouchableOpacity onPress={ () => this.control('shuffle') } style={globalStyle.controls_button}>
                    <AntDesign name="swap" size={26} color="white" />
                </TouchableOpacity>
                <TouchableOpacity onPress={ () => this.control('prev') } style={globalStyle.controls_button}>
                    <AntDesign name="stepbackward" size={38} color="white" />
                </TouchableOpacity>
                <TouchableOpacity onPress={ () => this.control('play')} style={globalStyle.controls_button}>
                    <AntDesign name={playingIcon} size={56} color="white" />
                </TouchableOpacity>
                <TouchableOpacity onPress={ () => this.control('next') } style={globalStyle.controls_button}>
                    <AntDesign name="stepforward" size={38} color="white" />
                </TouchableOpacity>
                <TouchableOpacity onPress={ () => this.control('repeat') } style={globalStyle.controls_button}>
                    <AntDesign name="retweet" size={26} color="white" />
                </TouchableOpacity>
            </Card>
        );
    }

    control(action) {
        switch(action) {
            case 'play':
                console.log('Pause/Play...');
                this.props.pause();
                break;
            case 'prev':
                console.log('Previous track...');
                this.props.previous();
                break;
            case 'next':
                console.log('Next track...');
                this.props.next();
                break;
            case 'shuffle':
                console.log('Toggled shuffle...');
                this.props.shuffle();
                break;
            case 'repeat':
                console.log('Toggled repeat...');
                this.props.repeat();
                break;
        }
    }
}

const styles = StyleSheet.create({
    song: {
        fontSize: 26,
        color: 'black',
        fontWeight: '500',
        textAlign: 'center',
        marginBottom: 6
    },
    artiste: {
        fontSize: 22,
        color: 'red',
        textAlign: "center",
        marginTop: 6,
        backgroundColor: '#eee',
        padding: 3,
        borderRadius: 10,
        marginHorizontal: -5
    },
    separator: {
        width: 70
    }
})
