import { StyleSheet, Text, View } from 'react-native';

export const globalStyle = StyleSheet.create({
    playerHeader: {
        // height: ,
        borderBottomWidth: 2,
        borderBottomColor: '#999',
        paddingVertical: 16,
        fontSize: 30
    },
    playerHeaderText: {
        color: 'red',
        fontSize: 16,
        textAlign: 'center'
    },
    basicCard: {
        padding: 6,
        borderRadius: 12,
        borderColor: 'white',
        borderWidth: 1
    },
    trackInfo: {
        borderColor: '#eee',
        borderWidth: 2,
        marginHorizontal: 7,
        justifyContent: 'center',
        backgroundColor: '#efefef'
    },
    separator: {
        backgroundColor: 'hotpink',
        height: 2,
        alignSelf: 'center',
        borderRadius: 10
    },
    controls: {
        justifyContent: 'space-evenly',
        flexDirection: 'row',
        marginVertical: 8,
        backgroundColor: '#efefef',
        marginHorizontal: 6,
        paddingVertical: 20
    }
})
